mapper.select:

        SELECT 
            T_USER.*
        FROM 
            T_USER
        WHERE
            T_USER.user_type = #{userType} 
            AND T_USER.gero_id = #{geroId}

		        AND T_USER.name LIKE '%' + #{name} + '%'


		        AND T_USER.gender = #{gender}


		        AND T_USER.identity_no like '%' + #{identityNo} + '%'

SELECT T_USER.* FROM T_USER WHERE T_USER.user_type = #{userType} AND T_USER.gero_id = #{geroId}
AND T_USER.name LIKE '%' + #{name} + '%'
AND T_USER.gender = #{gender}
AND T_USER.identity_no like '%' + #{identityNo} + '%'
AND T_USER.id IN ( SELECT DISTINCT(T_USER_ROLES.user_id) FROM (T_ROLE INNER JOIN T_USER_ROLES ON T_USER_ROLES.role_id = T_ROLE.id AND
T_ROLE.name = #{roleNameList[${index}]}
) )

mapper.update:
UPDATE T_STAFF
T_STAFF.name = #{name},
T_STAFF.nssf_id = #{nssfId},
T_STAFF.basic_url = #{basicUrl},
T_STAFF.archive_id = #{archiveId}
WHERE T_STAFF.id = #{id}


UPDATE
    T_STAFF
SET
    T_STAFF.leave_date = #{leaveDate}
WHERE
    T_STAFF.id = #{id}
