mapper.select:
SELECT
	
FROM T_ROLE a
WHERE a.name = #{name}

SELECT
	
FROM T_ROLE a
WHERE a.name = #{name}
	AND a.gero_id = #{geroId}

SELECT
FROM T_ROLE a LEFT JOIN T_USER_ROLES ur ON ur.role_id = a.id LEFT JOIN T_USER u ON u.id = ur.user_id WHERE
u.id = #{user.id}
AND u.username = #{user.username}
ORDER BY a.name

mapper.insert:
INSERT INTO T_ROLE(
	name,
	gero_id,
	notes
) VALUES ( 
	#{name}, 
	#{geroId}, 
	#{notes}
)

mapper.update:
UPDATE T_ROLE SET 
	name = #{name}, 
	gero_id = #{geroId}, 
	notes = #{notes}
WHERE id = #{id}
